SELECT a.id, article_title, is_top, a.is_delete, a.create_time, a.update_time, category_name, t.id AS tag_id, t.tag_name FROM ( SELECT id, article_title, is_top, is_draft, is_delete, create_time, update_time, category_id FROM tb_article
is_delete = #{condition.isDelete}
and is_draft = #{condition.isDraft}
and article_title like concat('%', #{condition.keywords}, '%')
ORDER BY is_top DESC, id DESC LIMIT #{condition.current}, #{condition.size} ) a LEFT JOIN tb_category c ON a.category_id = c.id LEFT JOIN tb_article_tag atg ON a.id = atg.article_id LEFT JOIN tb_tag t ON t.id = atg.tag_id ORDER BY is_top DESC, a.id DESC

SELECT count(1) from tb_article
is_delete = #{condition.isDelete}
and article_title like concat('%', #{condition.keywords}, '%')

