mapper.select:
SELECT * FROM ( SELECT c.user_id, u.nickname, u.avatar, u.web_site, c.reply_id, r.nickname AS reply_nickname, r.web_site AS reply_web_site, c.id, c.parent_id, c.comment_content, c.create_time, row_number () over ( PARTITION BY parent_id ORDER BY create_time ASC ) row_num FROM tb_comment c JOIN tb_user_info u ON c.user_id = u.id JOIN tb_user_info r ON c.reply_id = r.id WHERE c.is_delete = 0 AND parent_id IN (
#{commentId}
) ) t WHERE 4 > row_num

SELECT parent_id as comment_id, count(1) AS reply_count FROM tb_comment WHERE is_delete = 0 AND parent_id IN (
#{commentId}
) GROUP BY parent_id

SELECT c.id, u.avatar, u.nickname, r.nickname AS reply_nickname, a.article_title, c.comment_content, c.create_time, c.is_delete FROM tb_comment c LEFT JOIN tb_article a ON c.article_id = a.id LEFT JOIN tb_user_info u ON c.user_id = u.id LEFT JOIN tb_user_info r ON c.reply_id = r.id
c.is_delete = #{condition.isDelete}
and u.nickname like concat('%',#{condition.keywords},'%')
ORDER BY create_time DESC LIMIT #{condition.current},#{condition.size}

SELECT count(1) from tb_comment c LEFT JOIN tb_user_info u ON c.user_id = u.id
c.is_delete = #{condition.isDelete}
and u.nickname like concat('%',#{condition.keywords},'%')
